    private static String asString(Component jfDT) {
        return deepToString(jfDT).replaceAll("@[a-fA-F0-9]+", "@-");
    }

    private static String deepToString(Component component) {
        if (!(component instanceof Container)) {
            return String.format("%s{}", component.toString());
        }

        Container container = (Container) component;

        Component[] children;
        synchronized (container.getTreeLock()) {
            children = container.getComponents();
        }

        String childrenString = Arrays.stream(children)
                .map(EdgeConvertGUITest::deepToString)
                .collect(Collectors.joining(","));
        return String.format("%s{%s}", container.toString(), childrenString);
    }
